import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Tabs,
  Tab,
  Box,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Chip
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AdminPanel = () => {
  const [tab, setTab] = useState(0);
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [orders, setOrders] = useState([]);
  const [editDialog, setEditDialog] = useState(false);
  const [selectedBook, setSelectedBook] = useState(null);
  const [addDialog, setAddDialog] = useState(false);
  const [newBook, setNewBook] = useState({
    title: '',
    author: '',
    price: '',
    stock: '',
    category: '',
    description: '',
    image_url: ''
  });
  const navigate = useNavigate();

  useEffect(() => {
    const checkAuth = () => {
      try {
        const token = localStorage.getItem('token');
        const user = JSON.parse(localStorage.getItem('user') || '{}');

        if (!token) {
          throw new Error('Token bulunamadı');
        }

        if (!user || user.role !== 'admin') {
          throw new Error('Admin yetkisi yok');
        }
      } catch (error) {
        console.error('Yetkilendirme hatası:', error.message);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login');
      }
    };

    checkAuth();
    fetchData();

    // Tab değiştiğinde verileri yenile
    if (tab !== undefined) {
      fetchData();
    }
  }, [tab, navigate]);

  const fetchData = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Token bulunamadı');
      }

      const headers = {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      };

      let response;
      switch (tab) {
        case 0:
          response = await axios.get('http://localhost:5000/api/admin/books', { headers });
          setBooks(response.data);
          break;
        case 1:
          response = await axios.get('http://localhost:5000/api/admin/users', { headers });
          setUsers(response.data);
          break;
        case 2:
          response = await axios.get('http://localhost:5000/api/admin/orders', { headers });
          setOrders(response.data);
          break;
        default:
          break;
      }
    } catch (error) {
      console.error('Veri alınamadı:', error.response?.data || error.message);
      if (error.response?.status === 401 || error.response?.status === 403) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login');
      }
    }
  };

  const handleDeleteBook = async (bookId) => {
    if (!window.confirm('Bu kitabı silmek istediğinizden emin misiniz?')) return;

    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/api/books/${bookId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchData();
    } catch (error) {
      console.error('Kitap silinemedi:', error);
    }
  };

  const handleEditBook = (book) => {
    setSelectedBook(book);
    setEditDialog(true);
  };

  const handleSaveBook = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `http://localhost:5000/api/books/${selectedBook.id}`,
        selectedBook,
        { headers: { Authorization: `Bearer ${token}` }}
      );
      setEditDialog(false);
      fetchData();
    } catch (error) {
      console.error('Kitap güncellenemedi:', error);
    }
  };

  const handleUpdateOrderStatus = async (orderId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `http://localhost:5000/api/admin/orders/${orderId}`,
        { status: newStatus },
        { headers: { Authorization: `Bearer ${token}` }}
      );
      fetchData();
    } catch (error) {
      console.error('Sipariş durumu güncellenemedi:', error);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed': return 'success';
      case 'pending': return 'warning';
      case 'cancelled': return 'error';
      default: return 'default';
    }
  };

  const handleAddBook = async () => {
    try {
      // Zorunlu alanları kontrol et
      if (!newBook.title || !newBook.author || !newBook.price || !newBook.stock) {
        alert('Lütfen zorunlu alanları doldurun (Başlık, Yazar, Fiyat, Stok)');
        return;
      }

      // Veri tiplerini düzelt
      const bookData = {
        ...newBook,
        price: parseFloat(newBook.price),
        stock: parseInt(newBook.stock)
      };

      console.log('Gönderilen veri:', bookData); // Debug için

      if (isNaN(bookData.price) || bookData.price < 0) {
        alert('Lütfen geçerli bir fiyat girin');
        return;
      }

      if (isNaN(bookData.stock) || bookData.stock < 0) {
        alert('Lütfen geçerli bir stok miktarı girin');
        return;
      }

      const token = localStorage.getItem('token');
      const headers = {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      };

      const response = await axios.post(
        'http://localhost:5000/api/books',
        bookData,
        { headers }
      );

      if (response.status === 201) {
        alert('Kitap başarıyla eklendi');
        setAddDialog(false);
        setNewBook({
          title: '',
          author: '',
          price: '',
          stock: '',
          category: '',
          description: '',
          image_url: ''
        });
        fetchData();
      }
    } catch (error) {
      console.error('Kitap eklenemedi:', error);
      console.error('Hata detayı:', error.response?.data);
      alert(error.response?.data?.error || 'Kitap eklenirken bir hata oluştu');
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append('file', file);

      try {
        const token = localStorage.getItem('token');
        const response = await axios.post('http://localhost:5000/api/upload', formData, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setNewBook({ ...newBook, image_url: response.data.image_url });
      } catch (error) {
        console.error('Resim yüklenemedi:', error);
      }
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        Admin Paneli
      </Typography>

      <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
        <Tabs value={tab} onChange={(e, newValue) => setTab(newValue)}>
          <Tab label="Kitaplar" />
          <Tab label="Kullanıcılar" />
          <Tab label="Siparişler" />
        </Tabs>
      </Box>

      {tab === 0 && (
        <>
          <Box sx={{ mb: 2 }}>
            <Button
              variant="contained"
              color="primary"
              onClick={() => setAddDialog(true)}
            >
              Yeni Kitap Ekle
            </Button>
          </Box>

          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>ID</TableCell>
                  <TableCell>Başlık</TableCell>
                  <TableCell>Yazar</TableCell>
                  <TableCell>Fiyat</TableCell>
                  <TableCell>Stok</TableCell>
                  <TableCell>Satıcı</TableCell>
                  <TableCell>İşlemler</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {books.map((book) => (
                  <TableRow key={book.id}>
                    <TableCell>{book.id}</TableCell>
                    <TableCell>{book.title}</TableCell>
                    <TableCell>{book.author}</TableCell>
                    <TableCell>{book.price} TL</TableCell>
                    <TableCell>{book.stock}</TableCell>
                    <TableCell>{book.seller?.name}</TableCell>
                    <TableCell>
                      <IconButton onClick={() => handleEditBook(book)}>
                        <EditIcon />
                      </IconButton>
                      <IconButton onClick={() => handleDeleteBook(book.id)} color="error">
                        <DeleteIcon />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>

          <Dialog open={addDialog} onClose={() => setAddDialog(false)}>
            <DialogTitle>Yeni Kitap Ekle</DialogTitle>
            <DialogContent>
              <TextField
                fullWidth
                required
                label="Başlık"
                value={newBook.title}
                onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
                margin="normal"
                error={!newBook.title}
                helperText={!newBook.title ? 'Başlık zorunludur' : ''}
              />
              <TextField
                fullWidth
                required
                label="Yazar"
                value={newBook.author}
                onChange={(e) => setNewBook({ ...newBook, author: e.target.value })}
                margin="normal"
                error={!newBook.author}
                helperText={!newBook.author ? 'Yazar zorunludur' : ''}
              />
              <TextField
                fullWidth
                required
                label="Fiyat"
                type="number"
                inputProps={{ min: 0, step: "0.01" }}
                value={newBook.price}
                onChange={(e) => setNewBook({ ...newBook, price: e.target.value })}
                margin="normal"
                error={!newBook.price || parseFloat(newBook.price) < 0}
                helperText={!newBook.price ? 'Fiyat zorunludur' : parseFloat(newBook.price) < 0 ? 'Fiyat negatif olamaz' : ''}
              />
              <TextField
                fullWidth
                required
                label="Stok"
                type="number"
                inputProps={{ min: 0 }}
                value={newBook.stock}
                onChange={(e) => setNewBook({ ...newBook, stock: e.target.value })}
                margin="normal"
                error={!newBook.stock || parseInt(newBook.stock) < 0}
                helperText={!newBook.stock ? 'Stok zorunludur' : parseInt(newBook.stock) < 0 ? 'Stok negatif olamaz' : ''}
              />
              <TextField
                fullWidth
                label="Kategori"
                value={newBook.category}
                onChange={(e) => setNewBook({ ...newBook, category: e.target.value })}
                margin="normal"
              />
              <TextField
                fullWidth
                label="Açıklama"
                multiline
                rows={4}
                value={newBook.description}
                onChange={(e) => setNewBook({ ...newBook, description: e.target.value })}
                margin="normal"
              />
              <input
                accept="image/*"
                type="file"
                onChange={handleFileUpload}
                style={{ marginTop: 16 }}
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={() => setAddDialog(false)}>İptal</Button>
              <Button onClick={handleAddBook} variant="contained">
                Ekle
              </Button>
            </DialogActions>
          </Dialog>
        </>
      )}

      {tab === 1 && (
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>ID</TableCell>
                <TableCell>Ad</TableCell>
                <TableCell>Email</TableCell>
                <TableCell>Kayıt Tarihi</TableCell>
                <TableCell>Kitap Sayısı</TableCell>
                <TableCell>Sipariş Sayısı</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {users.map((user) => (
                <TableRow key={user.id}>
                  <TableCell>{user.id}</TableCell>
                  <TableCell>{user.name}</TableCell>
                  <TableCell>{user.email}</TableCell>
                  <TableCell>{new Date(user.created_at).toLocaleDateString('tr-TR')}</TableCell>
                  <TableCell>{user.book_count}</TableCell>
                  <TableCell>{user.order_count}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}

      {tab === 2 && (
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Sipariş ID</TableCell>
                <TableCell>Kullanıcı</TableCell>
                <TableCell>Tutar</TableCell>
                <TableCell>Durum</TableCell>
                <TableCell>Tarih</TableCell>
                <TableCell>İşlemler</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {orders.map((order) => (
                <TableRow key={order.id}>
                  <TableCell>{order.id}</TableCell>
                  <TableCell>{order.user.name}</TableCell>
                  <TableCell>{order.total_amount} TL</TableCell>
                  <TableCell>
                    <Chip
                      label={order.status === 'completed' ? 'Tamamlandı' : 
                             order.status === 'pending' ? 'Beklemede' : 'İptal Edildi'}
                      color={getStatusColor(order.status)}
                      size="small"
                    />
                  </TableCell>
                  <TableCell>{new Date(order.created_at).toLocaleDateString('tr-TR')}</TableCell>
                  <TableCell>
                    <Button
                      size="small"
                      onClick={() => handleUpdateOrderStatus(order.id, 'completed')}
                      disabled={order.status === 'completed'}
                    >
                      Tamamla
                    </Button>
                    <Button
                      size="small"
                      color="error"
                      onClick={() => handleUpdateOrderStatus(order.id, 'cancelled')}
                      disabled={order.status === 'cancelled'}
                    >
                      İptal Et
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}

      <Dialog open={editDialog} onClose={() => setEditDialog(false)}>
        <DialogTitle>Kitap Düzenle</DialogTitle>
        <DialogContent>
          {selectedBook && (
            <>
              <TextField
                fullWidth
                label="Başlık"
                value={selectedBook.title}
                onChange={(e) => setSelectedBook({ ...selectedBook, title: e.target.value })}
                margin="normal"
              />
              <TextField
                fullWidth
                label="Yazar"
                value={selectedBook.author}
                onChange={(e) => setSelectedBook({ ...selectedBook, author: e.target.value })}
                margin="normal"
              />
              <TextField
                fullWidth
                label="Fiyat"
                type="number"
                value={selectedBook.price}
                onChange={(e) => setSelectedBook({ ...selectedBook, price: e.target.value })}
                margin="normal"
              />
              <TextField
                fullWidth
                label="Stok"
                type="number"
                value={selectedBook.stock}
                onChange={(e) => setSelectedBook({ ...selectedBook, stock: e.target.value })}
                margin="normal"
              />
              <TextField
                fullWidth
                label="Kategori"
                value={selectedBook.category || ''}
                onChange={(e) => setSelectedBook({ ...selectedBook, category: e.target.value })}
                margin="normal"
              />
            </>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setEditDialog(false)}>İptal</Button>
          <Button onClick={handleSaveBook} variant="contained">
            Kaydet
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default AdminPanel;
