import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Grid, 
  Card, 
  CardContent, 
  CardMedia, 
  Typography, 
  Button,
  TextField,
  Box,
  InputAdornment,
  FormControl,
  Select,
  MenuItem
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import axios from 'axios';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchBy, setSearchBy] = useState('title'); // 'title' veya 'author'

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/books');
        setBooks(response.data);
        setFilteredBooks(response.data);
      } catch (error) {
        console.error('Kitaplar yüklenirken hata oluştu:', error);
      }
    };

    fetchBooks();
  }, []);

  // Arama işlevi
  const handleSearch = (event) => {
    const term = event.target.value.toLowerCase();
    setSearchTerm(term);

    const filtered = books.filter(book => {
      if (searchBy === 'title') {
        return book.title.toLowerCase().includes(term);
      } else {
        return book.author.toLowerCase().includes(term);
      }
    });

    setFilteredBooks(filtered);
  };

  // Arama türü değiştirme
  const handleSearchByChange = (event) => {
    setSearchBy(event.target.value);
    setSearchTerm(''); // Arama türü değişince aramayı sıfırla
    setFilteredBooks(books); // Listeyi sıfırla
  };

  const addToCart = (book) => {
    if (book.stock <= 0) {
      alert('Bu ürün stokta yok!');
      return;
    }

    const currentCart = JSON.parse(localStorage.getItem('cart') || '[]');
    const existingItem = currentCart.find(item => item.id === book.id);
    
    if (existingItem) {
      // Stok kontrolü
      if (existingItem.quantity >= book.stock) {
        alert('Üzgünüz, stokta yeterli ürün yok!');
        return;
      }
      
      const updatedCart = currentCart.map(item =>
        item.id === book.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      );
      localStorage.setItem('cart', JSON.stringify(updatedCart));
    } else {
      const updatedCart = [...currentCart, { ...book, quantity: 1 }];
      localStorage.setItem('cart', JSON.stringify(updatedCart));
    }
    
    alert('Ürün sepete eklendi!');
  };

  return (
    <Container sx={{ py: 4 }}>
      <Typography variant="h4" component="h2" gutterBottom>
        Kitaplar
      </Typography>

      {/* Arama Bölümü */}
      <Box sx={{ mb: 4, display: 'flex', gap: 2 }}>
        <FormControl sx={{ minWidth: 120 }}>
          <Select
            value={searchBy}
            onChange={handleSearchByChange}
            size="small"
          >
            <MenuItem value="title">Kitap Adı</MenuItem>
            <MenuItem value="author">Yazar</MenuItem>
          </Select>
        </FormControl>

        <TextField
          fullWidth
          variant="outlined"
          size="small"
          placeholder={searchBy === 'title' ? 'Kitap adı ara...' : 'Yazar ara...'}
          value={searchTerm}
          onChange={handleSearch}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon />
              </InputAdornment>
            ),
          }}
        />
      </Box>

      {/* Sonuç sayısını göster */}
      <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
        {filteredBooks.length} sonuç bulundu
      </Typography>

      {/* Kitap Listesi */}
      <Grid container spacing={4}>
        {filteredBooks.map((book) => (
          <Grid item key={book.id} xs={12} sm={6} md={4}>
            <Card>
              <CardMedia
                component="img"
                height="200"
                image={book.image_url || `https://picsum.photos/seed/${book.id}/200/300`}
                alt={book.title}
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="div">
                  {book.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Yazar: {book.author}
                </Typography>
                <Typography variant="h6" color="primary">
                  {book.price} TL
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Stok: {book.stock}
                </Typography>
                <Button 
                  variant="contained" 
                  color="primary" 
                  fullWidth 
                  sx={{ mt: 2 }}
                  onClick={() => addToCart(book)}
                  disabled={book.stock <= 0}
                >
                  {book.stock <= 0 ? 'Stokta Yok' : 'Sepete Ekle'}
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Sonuç bulunamazsa */}
      {filteredBooks.length === 0 && (
        <Typography variant="h6" align="center" sx={{ mt: 4 }}>
          Sonuç bulunamadı
        </Typography>
      )}
    </Container>
  );
};

export default BookList;
