import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link as RouterLink } from 'react-router-dom';
import { 
  Container, 
  Grid, 
  Paper, 
  Typography, 
  Button, 
  Box, 
  Rating, 
  Chip,
  Breadcrumbs,
  Link,
  Divider,
  Skeleton,
  Alert,
  Snackbar,
  IconButton,
  Avatar,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField
} from '@mui/material';
import {
  ShoppingCart as CartIcon,
  NavigateNext as NavigateNextIcon,
  Warning as WarningIcon,
  FavoriteBorder as FavoriteBorderIcon,
  Favorite as FavoriteIcon,
  Star as StarIcon
} from '@mui/icons-material';
import axios from '../utils/axiosConfig';
import { createSlug } from '../utils/helpers';

const BookDetail = () => {
  const { id, title } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'info'
  });
  const [reviews, setReviews] = useState([]);
  const [userReview, setUserReview] = useState({ rating: 0, comment: '' });
  const [isInWishlist, setIsInWishlist] = useState(false);
  const [openReviewDialog, setOpenReviewDialog] = useState(false);

  useEffect(() => {
    const fetchBookDetails = async () => {
      try {
        setLoading(true);
        console.log('Fetching book details for ID:', id);
        
        const response = await axios.get(`/api/books/${id}`);
        console.log('Book details response:', response);

        if (response.data) {
          setBook(response.data);
          
          // URL'deki title ile kitap başlığı uyuşmuyorsa yönlendir
          const slug = createSlug(response.data.title);
          if (slug !== title) {
            navigate(`/book/${slug}/${id}`, { replace: true });
          }
        } else {
          setError('Kitap bulunamadı');
        }
      } catch (error) {
        console.error('Error fetching book details:', error);
        setError('Kitap yüklenirken bir hata oluştu');
      } finally {
        setLoading(false);
      }
    };

    if (id) {
      fetchBookDetails();
    }
  }, [id, title, navigate]);

  // Yorumları getir
  const fetchReviews = async () => {
    try {
      const response = await axios.get(`/api/books/${id}/reviews`);
      setReviews(response.data);
    } catch (error) {
      console.error('Yorumlar yüklenemedi:', error);
    }
  };

  // İstek listesi durumunu kontrol et
  const checkWishlist = async () => {
    try {
      const response = await axios.get('/api/wishlist');
      setIsInWishlist(response.data.some(item => item.book.id === parseInt(id)));
    } catch (error) {
      console.error('İstek listesi kontrolü başarısız:', error);
    }
  };

  useEffect(() => {
    fetchReviews();
    checkWishlist();
  }, [id]);

  // İstek listesine ekle/çıkar
  const handleWishlist = async () => {
    try {
      await axios.post(`/api/wishlist/${id}`);
      setIsInWishlist(!isInWishlist);
      setSnackbar({
        open: true,
        message: isInWishlist ? 'Kitap istek listesinden çıkarıldı' : 'Kitap istek listesine eklendi',
        severity: 'success'
      });
    } catch (error) {
      console.error('İstek listesi işlemi başarısız:', error);
      setSnackbar({
        open: true,
        message: 'Bir hata oluştu',
        severity: 'error'
      });
    }
  };

  // Yorum ekle/güncelle
  const handleReviewSubmit = async () => {
    try {
      await axios.post(`/api/books/${id}/reviews`, userReview);
      setOpenReviewDialog(false);
      fetchReviews();
      setSnackbar({
        open: true,
        message: 'Yorumunuz kaydedildi',
        severity: 'success'
      });
    } catch (error) {
      console.error('Yorum eklenemedi:', error);
      setSnackbar({
        open: true,
        message: 'Yorum eklenirken bir hata oluştu',
        severity: 'error'
      });
    }
  };

  // Loading durumu
  if (loading) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4 }}>
        <Grid container spacing={4}>
          <Grid item xs={12} md={5}>
            <Skeleton variant="rectangular" height={500} />
          </Grid>
          <Grid item xs={12} md={7}>
            <Skeleton variant="text" height={60} />
            <Skeleton variant="text" height={30} />
            <Skeleton variant="text" height={30} />
          </Grid>
        </Grid>
      </Container>
    );
  }

  // Hata durumu
  if (error) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4 }}>
        <Paper sx={{ p: 3, textAlign: 'center' }}>
          <WarningIcon sx={{ fontSize: 60, color: 'error.main', mb: 2 }} />
          <Typography variant="h6" gutterBottom>
            {error}
          </Typography>
          <Button 
            variant="contained" 
            onClick={() => navigate('/books')}
            sx={{ mt: 2 }}
          >
            Kitaplara Dön
          </Button>
        </Paper>
      </Container>
    );
  }

  // Kitap bulunamadı durumu
  if (!book) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4 }}>
        <Paper sx={{ p: 3, textAlign: 'center' }}>
          <Typography variant="h6" gutterBottom>
            Kitap bulunamadı
          </Typography>
          <Button 
            variant="contained" 
            onClick={() => navigate('/books')}
            sx={{ mt: 2 }}
          >
            Kitaplara Dön
          </Button>
        </Paper>
      </Container>
    );
  }

  // Normal render
  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      {/* Breadcrumbs */}
      <Breadcrumbs 
        separator={<NavigateNextIcon fontSize="small" />} 
        sx={{ mb: 4 }}
      >
        <Link component={RouterLink} to="/" color="inherit">
          Ana Sayfa
        </Link>
        <Link component={RouterLink} to="/books" color="inherit">
          Kitaplar
        </Link>
        <Typography color="text.primary">
          {book?.title}
        </Typography>
      </Breadcrumbs>

      <Grid container spacing={4}>
        {/* Sol Taraf - Kitap Resmi */}
        <Grid item xs={12} md={5}>
          <Paper 
            sx={{ 
              p: 2, 
              height: '100%', 
              display: 'flex', 
              alignItems: 'center', 
              justifyContent: 'center' 
            }}
          >
            <img
              src={book?.image_url || '/placeholder.jpg'}
              alt={book?.title}
              style={{ 
                maxWidth: '100%', 
                maxHeight: '500px', 
                objectFit: 'contain' 
              }}
            />
          </Paper>
        </Grid>

        {/* Sağ Taraf - Kitap Bilgileri */}
        <Grid item xs={12} md={7}>
          <Box>
            <Typography variant="h4" gutterBottom>
              {book?.title}
            </Typography>
            
            <Typography variant="h6" color="text.secondary" gutterBottom>
              {book?.author}
            </Typography>

            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <Rating value={4.5} precision={0.5} readOnly />
              <Typography variant="body2" sx={{ ml: 1 }}>
                (128 değerlendirme)
              </Typography>
            </Box>

            <Box sx={{ mb: 3 }}>
              <Chip 
                label={book?.category} 
                color="primary" 
                variant="outlined" 
                size="small" 
              />
              <Chip 
                label={book?.publisher?.name || 'Belirtilmemiş'} 
                variant="outlined" 
                size="small" 
                sx={{ ml: 1 }} 
              />
            </Box>

            <Typography variant="h5" color="primary" sx={{ mb: 3 }}>
              {book?.price} TL
            </Typography>

            <Box sx={{ mb: 3 }}>
              {book?.stock > 0 ? (
                <Button
                  variant="contained"
                  size="large"
                  startIcon={<CartIcon />}
                  onClick={() => {
                    const token = localStorage.getItem('token');
                    if (!token) {
                      setSnackbar({
                        open: true,
                        message: 'Lütfen önce giriş yapın',
                        severity: 'warning'
                      });
                      setTimeout(() => navigate('/login'), 1500);
                    } else {
                      axios.post('/api/cart', {
                        book_id: parseInt(id),
                        quantity: 1
                      })
                      .then(response => {
                        if (response.data) {
                          setSnackbar({
                            open: true,
                            message: 'Kitap sepete eklendi',
                            severity: 'success'
                          });
                          setTimeout(() => navigate('/cart'), 1500);
                        }
                      })
                      .catch(error => {
                        console.error('Sepete eklenemedi:', error);
                        const errorMessage = error.response?.data?.message || 'Kitap sepete eklenirken bir hata oluştu';
                        setSnackbar({
                          open: true,
                          message: errorMessage,
                          severity: 'error'
                        });
                      });
                    }
                  }}
                  sx={{ minWidth: 200 }}
                >
                  Sepete Ekle
                </Button>
              ) : (
                <Chip
                  label="Tükendi"
                  color="error"
                  sx={{ height: 40 }}
                />
              )}
            </Box>

            <Divider sx={{ my: 3 }} />

            <Typography variant="h6" gutterBottom>
              Kitap Açıklaması
            </Typography>
            <Typography variant="body1">
              {book?.description || 'Bu kitap için açıklama bulunmuyor.'}
            </Typography>

            <Divider sx={{ my: 3 }} />

            <Grid container spacing={2}>
              <Grid item xs={6}>
                <Typography variant="subtitle2" color="text.secondary">
                  Stok Durumu
                </Typography>
                <Typography>
                  {book?.stock > 0 ? `${book.stock} adet` : 'Tükendi'}
                </Typography>
              </Grid>
              <Grid item xs={6}>
                <Typography variant="subtitle2" color="text.secondary">
                  Satıcı
                </Typography>
                <Typography>
                  {book?.seller?.name || 'Belirtilmemiş'}
                </Typography>
              </Grid>
            </Grid>
          </Box>
        </Grid>
      </Grid>

      {/* İstek Listesi Butonu */}
      <IconButton 
        onClick={handleWishlist}
        sx={{ position: 'absolute', top: 8, right: 8 }}
      >
        {isInWishlist ? (
          <FavoriteIcon color="error" />
        ) : (
          <FavoriteBorderIcon />
        )}
      </IconButton>

      {/* Yorumlar Bölümü */}
      <Paper sx={{ mt: 4, p: 3 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Typography variant="h6">
            Yorumlar ({reviews.length})
          </Typography>
          <Button 
            variant="contained"
            onClick={() => setOpenReviewDialog(true)}
          >
            Yorum Yap
          </Button>
        </Box>

        {reviews.map((review) => (
          <Paper key={review.id} sx={{ p: 2, mb: 2 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
              <Avatar src={review.user.avatar} />
              <Box sx={{ ml: 2 }}>
                <Typography variant="subtitle1">
                  {review.user.username}
                </Typography>
                <Rating value={review.rating} readOnly size="small" />
              </Box>
            </Box>
            <Typography variant="body1">
              {review.comment}
            </Typography>
            <Typography variant="caption" color="text.secondary">
              {new Date(review.created_at).toLocaleDateString()}
            </Typography>
          </Paper>
        ))}
      </Paper>

      {/* Yorum Ekleme Dialog'u */}
      <Dialog open={openReviewDialog} onClose={() => setOpenReviewDialog(false)}>
        <DialogTitle>Yorum Yap</DialogTitle>
        <DialogContent>
          <Box sx={{ mb: 2, mt: 1 }}>
            <Typography>Puanınız</Typography>
            <Rating
              value={userReview.rating}
              onChange={(event, newValue) => {
                setUserReview(prev => ({ ...prev, rating: newValue }));
              }}
            />
          </Box>
          <TextField
            fullWidth
            multiline
            rows={4}
            label="Yorumunuz"
            value={userReview.comment}
            onChange={(e) => setUserReview(prev => ({ ...prev, comment: e.target.value }))}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenReviewDialog(false)}>İptal</Button>
          <Button 
            onClick={handleReviewSubmit}
            variant="contained"
            disabled={!userReview.rating || !userReview.comment}
          >
            Gönder
          </Button>
        </DialogActions>
      </Dialog>

      {/* Snackbar bildirimleri */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={() => setSnackbar(prev => ({ ...prev, open: false }))}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert 
          onClose={() => setSnackbar(prev => ({ ...prev, open: false }))} 
          severity={snackbar.severity}
          variant="filled"
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default BookDetail;
