import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Box,
  Avatar,
  Grid,
  Divider,
  Alert,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions
} from '@mui/material';
import { PhotoCamera, Edit } from '@mui/icons-material';
import axios from '../utils/axiosConfig';

const Profile = () => {
  const [profile, setProfile] = useState({
    username: '',
    name: '',
    email: '',
    balance: 0,
    avatar: null
  });
  const [editing, setEditing] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [openUsernameDialog, setOpenUsernameDialog] = useState(false);
  const [newUsername, setNewUsername] = useState('');

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      const response = await axios.get('/api/user/info');
      setProfile(response.data);
      setNewUsername(response.data.username);
    } catch (error) {
      setError('Profil bilgileri alınamadı');
    }
  };

  const handleUpdateProfile = async () => {
    try {
      const response = await axios.put('/api/user/profile', {
        name: profile.name
      });
      setSuccess('Profil güncellendi');
      setEditing(false);
      
      // Local storage'ı güncelle
      const user = JSON.parse(localStorage.getItem('user'));
      localStorage.setItem('user', JSON.stringify({ ...user, name: profile.name }));
    } catch (error) {
      setError('Profil güncellenemedi');
    }
  };

  const handleUpdateUsername = async () => {
    try {
      const response = await axios.put('/api/user/profile', {
        username: newUsername
      });
      setSuccess('Kullanıcı adı güncellendi');
      setOpenUsernameDialog(false);
      setProfile(prev => ({ ...prev, username: newUsername }));
      
      // Local storage'ı güncelle
      const user = JSON.parse(localStorage.getItem('user'));
      localStorage.setItem('user', JSON.stringify({ ...user, username: newUsername }));
    } catch (error) {
      setError('Kullanıcı adı güncellenemedi');
    }
  };

  const handleAvatarChange = async (event) => {
    const file = event.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append('avatar', file);

      try {
        const response = await axios.post('/api/user/avatar', formData);
        setProfile(prev => ({ ...prev, avatar: response.data.avatar_url }));
        setSuccess('Profil fotoğrafı güncellendi');
      } catch (error) {
        setError('Profil fotoğrafı yüklenemedi');
      }
    }
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Paper sx={{ p: 4 }}>
        <Grid container spacing={4}>
          {/* Sol taraf - Avatar ve temel bilgiler */}
          <Grid item xs={12} md={4}>
            <Box sx={{ textAlign: 'center' }}>
              <Box sx={{ position: 'relative', display: 'inline-block' }}>
                <Avatar
                  src={profile.avatar}
                  sx={{ width: 150, height: 150, mb: 2 }}
                />
                <IconButton
                  component="label"
                  sx={{
                    position: 'absolute',
                    bottom: 16,
                    right: -8,
                    backgroundColor: 'primary.main',
                    '&:hover': { backgroundColor: 'primary.dark' }
                  }}
                >
                  <input
                    hidden
                    accept="image/*"
                    type="file"
                    onChange={handleAvatarChange}
                  />
                  <PhotoCamera sx={{ color: 'white' }} />
                </IconButton>
              </Box>
              <Typography variant="h6" gutterBottom>
                {profile.name}
              </Typography>
              <Typography color="textSecondary" gutterBottom>
                @{profile.username}
              </Typography>
              <Typography variant="body1" sx={{ mt: 2 }}>
                Bakiye: {profile.balance?.toFixed(2)} TL
              </Typography>
            </Box>
          </Grid>

          {/* Sağ taraf - Düzenlenebilir bilgiler */}
          <Grid item xs={12} md={8}>
            {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
            {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}

            <Box sx={{ mb: 3 }}>
              <Typography variant="h6" gutterBottom>
                Profil Bilgileri
              </Typography>
              <TextField
                fullWidth
                label="E-posta"
                value={profile.email}
                disabled
                margin="normal"
              />
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <TextField
                  fullWidth
                  label="Kullanıcı Adı"
                  value={profile.username}
                  disabled
                  margin="normal"
                />
                <Button
                  startIcon={<Edit />}
                  onClick={() => setOpenUsernameDialog(true)}
                  sx={{ ml: 1 }}
                >
                  Değiştir
                </Button>
              </Box>
              <TextField
                fullWidth
                label="Ad Soyad"
                value={profile.name}
                onChange={(e) => setProfile({ ...profile, name: e.target.value })}
                disabled={!editing}
                margin="normal"
              />
            </Box>

            <Box sx={{ mt: 3 }}>
              {!editing ? (
                <Button
                  variant="contained"
                  onClick={() => setEditing(true)}
                  startIcon={<Edit />}
                >
                  Profili Düzenle
                </Button>
              ) : (
                <>
                  <Button
                    variant="contained"
                    onClick={handleUpdateProfile}
                    sx={{ mr: 1 }}
                  >
                    Kaydet
                  </Button>
                  <Button
                    variant="outlined"
                    onClick={() => {
                      setEditing(false);
                      fetchProfile();
                    }}
                  >
                    İptal
                  </Button>
                </>
              )}
            </Box>
          </Grid>
        </Grid>
      </Paper>

      {/* Kullanıcı adı değiştirme dialog'u */}
      <Dialog open={openUsernameDialog} onClose={() => setOpenUsernameDialog(false)}>
        <DialogTitle>Kullanıcı Adını Değiştir</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            label="Yeni Kullanıcı Adı"
            value={newUsername}
            onChange={(e) => setNewUsername(e.target.value)}
            margin="normal"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenUsernameDialog(false)}>İptal</Button>
          <Button onClick={handleUpdateUsername} variant="contained">
            Güncelle
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Profile;
