import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Typography, 
  Table, 
  TableBody, 
  TableCell, 
  TableContainer, 
  TableHead, 
  TableRow, 
  Paper, 
  Button,
  IconButton 
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // Local storage'dan sepet verilerini al
    const savedCart = JSON.parse(localStorage.getItem('cart') || '[]');
    setCartItems(savedCart);
  }, []);

  const removeFromCart = (bookId) => {
    const updatedCart = cartItems.filter(item => item.id !== bookId);
    setCartItems(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
  };

  const updateQuantity = (bookId, change) => {
    const updatedCart = cartItems.map(item => {
      if (item.id === bookId) {
        const newQuantity = Math.max(1, item.quantity + change);
        
        // Stok kontrolü
        if (newQuantity > item.stock) {
          alert('Üzgünüz, stokta yeterli ürün yok!');
          return item;
        }
        
        return { ...item, quantity: newQuantity };
      }
      return item;
    });
    setCartItems(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
  };

  const totalPrice = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <Container sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom>
        Sepetim
      </Typography>

      {cartItems.length === 0 ? (
        <Typography>Sepetiniz boş</Typography>
      ) : (
        <>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Ürün</TableCell>
                  <TableCell>Fiyat</TableCell>
                  <TableCell>Adet</TableCell>
                  <TableCell>Toplam</TableCell>
                  <TableCell>İşlemler</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {cartItems.map((item) => (
                  <TableRow key={item.id}>
                    <TableCell>
                      <div style={{ display: 'flex', alignItems: 'center' }}>
                        {item.image_url && (
                          <img 
                            src={item.image_url} 
                            alt={item.title} 
                            style={{ height: '50px', marginRight: '10px' }}
                          />
                        )}
                        <div>
                          <Typography variant="subtitle1">{item.title}</Typography>
                          <Typography variant="body2" color="text.secondary">
                            {item.author}
                          </Typography>
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>{item.price} TL</TableCell>
                    <TableCell>
                      <Button 
                        size="small" 
                        onClick={() => updateQuantity(item.id, -1)}
                        disabled={item.quantity <= 1}
                      >
                        -
                      </Button>
                      {item.quantity}
                      <Button 
                        size="small" 
                        onClick={() => updateQuantity(item.id, 1)}
                        disabled={item.quantity >= item.stock}
                      >
                        +
                      </Button>
                      <Typography variant="caption" display="block" color="text.secondary">
                        Stokta: {item.stock}
                      </Typography>
                    </TableCell>
                    <TableCell>{(item.price * item.quantity).toFixed(2)} TL</TableCell>
                    <TableCell>
                      <IconButton 
                        color="error" 
                        onClick={() => removeFromCart(item.id)}
                      >
                        <DeleteIcon />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>

          <Paper sx={{ mt: 2, p: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <Typography variant="h6">
              Toplam: {totalPrice.toFixed(2)} TL
            </Typography>
            <Button variant="contained" color="primary">
              Satın Al
            </Button>
          </Paper>
        </>
      )}
    </Container>
  );
};

export default Cart;
