export const createSlug = (text) => {
  const turkishChars = { 'ı': 'i', 'ğ': 'g', 'ü': 'u', 'ş': 's', 'ö': 'o', 'ç': 'c' };
  return text
    .toLowerCase()
    .replace(/[ıüşöçğ]/g, letter => turkishChars[letter])
    .replace(/[^a-z0-9]/g, '-')
    .replace(/-+/g, '-')
    .replace(/^-|-$/g, '');
};
