import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Box,
  Paper,
  Divider,
  Chip,
  Rating,
  useTheme,
  useMediaQuery,
  Skeleton,
  IconButton,
  Tabs,
  Tab
} from '@mui/material';
import {
  ShoppingCart as CartIcon,
  ArrowForward as ArrowForwardIcon,
  Favorite as FavoriteIcon,
  LocalOffer as LocalOfferIcon,
  NewReleases as NewReleasesIcon,
  TrendingUp as TrendingUpIcon
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import axios from '../utils/axiosConfig';

const Home = () => {
  const [newBooks, setNewBooks] = useState([]);
  const [trendingBooks, setTrendingBooks] = useState([]);
  const [discountedBooks, setDiscountedBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState(0);
  
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const navigate = useNavigate();

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const [newResponse, trendingResponse, discountedResponse] = await Promise.all([
        axios.get('/api/books/new'),
        axios.get('/api/books/trending'),
        axios.get('/api/books/discounted')
      ]);

      setNewBooks(newResponse.data);
      setTrendingBooks(trendingResponse.data);
      setDiscountedBooks(discountedResponse.data);
      setLoading(false);
    } catch (error) {
      console.error('Kitaplar yüklenemedi:', error);
      setLoading(false);
    }
  };

  const handleAddToCart = async (bookId) => {
    try {
      await axios.post('/api/cart/add', { book_id: bookId, quantity: 1 });
      // Başarılı bildirim göster
    } catch (error) {
      console.error('Sepete eklenemedi:', error);
    }
  };

  const BookCard = ({ book }) => (
    <Card 
      sx={{ 
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        transition: 'transform 0.2s',
        '&:hover': {
          transform: 'translateY(-5px)',
          boxShadow: 6,
          cursor: 'pointer'
        }
      }}
      onClick={() => navigate(`/books/${book.id}`)}
    >
      <Box sx={{ position: 'relative' }}>
        <CardMedia
          component="img"
          height="260"
          image={book.image_url || '/placeholder.jpg'}
          alt={book.title}
          sx={{ objectFit: 'contain', p: 2 }}
        />
        {book.discount > 0 && (
          <Chip
            label={`%${book.discount} İndirim`}
            color="error"
            size="small"
            sx={{
              position: 'absolute',
              top: 8,
              right: 8
            }}
          />
        )}
      </Box>
      <CardContent sx={{ flexGrow: 1, pt: 1 }}>
        <Typography 
          variant="subtitle1" 
          sx={{ 
            fontWeight: 'bold',
            height: '2.4em',
            overflow: 'hidden',
            textOverflow: 'ellipsis',
            display: '-webkit-box',
            WebkitLineClamp: 2,
            WebkitBoxOrient: 'vertical'
          }}
        >
          {book.title}
        </Typography>
        <Typography 
          variant="body2" 
          color="text.secondary"
          sx={{ mb: 1 }}
        >
          {book.author}
        </Typography>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
          <Rating value={book.rating || 4.5} precision={0.5} size="small" readOnly />
          <Typography variant="caption" sx={{ ml: 1 }}>
            ({book.review_count || 0})
          </Typography>
        </Box>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Box>
            {book.discount > 0 && (
              <Typography 
                variant="caption" 
                sx={{ 
                  textDecoration: 'line-through',
                  color: 'text.secondary',
                  mr: 1
                }}
              >
                {book.original_price} TL
              </Typography>
            )}
            <Typography variant="h6" color="primary" sx={{ fontWeight: 'bold' }}>
              {book.price} TL
            </Typography>
          </Box>
          <IconButton
            color="primary"
            onClick={(e) => {
              e.stopPropagation();
              handleAddToCart(book.id);
            }}
            disabled={book.stock <= 0}
          >
            <CartIcon />
          </IconButton>
        </Box>
      </CardContent>
    </Card>
  );

  const BookSection = ({ title, icon, books, color }) => (
    <Paper sx={{ p: 3, mb: 4 }}>
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        mb: 3
      }}>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          {icon}
          <Typography variant="h5" component="h2">
            {title}
          </Typography>
        </Box>
        <Button 
          endIcon={<ArrowForwardIcon />}
          onClick={() => navigate('/books')}
        >
          Tümünü Gör
        </Button>
      </Box>
      <Grid container spacing={3}>
        {books.slice(0, 4).map((book) => (
          <Grid item xs={12} sm={6} md={3} key={book.id}>
            <BookCard book={book} />
          </Grid>
        ))}
      </Grid>
    </Paper>
  );

  return (
    <Container maxWidth="xl" sx={{ mt: 4, mb: 4 }}>
      {/* Hero Banner */}
      <Paper 
        sx={{ 
          p: 4, 
          mb: 4, 
          background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
          color: 'white',
          textAlign: 'center'
        }}
      >
        <Typography variant="h3" component="h1" gutterBottom>
          Kitap Dünyasına Hoş Geldiniz
        </Typography>
        <Typography variant="h6" sx={{ mb: 3 }}>
          Binlerce kitap, uygun fiyatlar ve hızlı teslimat
        </Typography>
        <Button 
          variant="contained" 
          color="secondary" 
          size="large"
          onClick={() => navigate('/books')}
        >
          Hemen Keşfet
        </Button>
      </Paper>

      {/* Kitap Bölümleri */}
      <BookSection 
        title="Yeni Çıkanlar" 
        icon={<NewReleasesIcon color="primary" />} 
        books={newBooks}
      />
      
      <BookSection 
        title="Çok Satanlar" 
        icon={<TrendingUpIcon sx={{ color: 'success.main' }} />} 
        books={trendingBooks}
      />
      
      <BookSection 
        title="İndirimli Kitaplar" 
        icon={<LocalOfferIcon color="error" />} 
        books={discountedBooks}
      />
    </Container>
  );
};

export default Home;
