import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Box,
  TextField,
  MenuItem,
  IconButton,
  Snackbar,
  Alert
} from '@mui/material';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import axios from 'axios';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'success'
  });

  useEffect(() => {
    fetchBooks();
  }, [selectedCategory]);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/books');
      setBooks(response.data);
      
      // Kategorileri topla
      const uniqueCategories = [...new Set(response.data.map(book => book.category))];
      setCategories(uniqueCategories.filter(Boolean));
    } catch (error) {
      console.error('Kitaplar alınamadı:', error);
    }
  };

  const addToCart = async (book) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setSnackbar({
          open: true,
          message: 'Lütfen önce giriş yapın',
          severity: 'warning'
        });
        return;
      }

      await axios.post('http://localhost:5000/api/cart', 
        { book_id: book.id, quantity: 1 },
        { headers: { Authorization: `Bearer ${token}` }}
      );

      setSnackbar({
        open: true,
        message: 'Ürün sepete eklendi',
        severity: 'success'
      });

      // Sepet sayısını güncellemek için event fırlat
      window.dispatchEvent(new Event('cartUpdate'));
    } catch (error) {
      setSnackbar({
        open: true,
        message: error.response?.data?.error || 'Bir hata oluştu',
        severity: 'error'
      });
    }
  };

  const filteredBooks = books.filter(book => {
    const matchesCategory = !selectedCategory || book.category === selectedCategory;
    const matchesSearch = book.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         book.author.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Box sx={{ mb: 3, display: 'flex', gap: 2 }}>
        <TextField
          label="Ara"
          variant="outlined"
          size="small"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          sx={{ flexGrow: 1 }}
        />
        <TextField
          select
          label="Kategori"
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          size="small"
          sx={{ minWidth: 200 }}
        >
          <MenuItem value="">Tümü</MenuItem>
          {categories.map((category) => (
            <MenuItem key={category} value={category}>
              {category}
            </MenuItem>
          ))}
        </TextField>
      </Box>

      <Grid container spacing={3}>
        {filteredBooks.map((book) => (
          <Grid item xs={12} sm={6} md={4} key={book.id}>
            <Card>
              <CardMedia
                component="img"
                height="200"
                image={book.image_url || '/placeholder.png'}
                alt={book.title}
              />
              <CardContent>
                <Typography gutterBottom variant="h6" component="div">
                  {book.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {book.author}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Kategori: {book.category || 'Belirtilmemiş'}
                </Typography>
                <Box sx={{ mt: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <Typography variant="h6" color="primary">
                    {book.price.toFixed(2)} TL
                  </Typography>
                  <IconButton 
                    color="primary"
                    onClick={() => addToCart(book)}
                    disabled={book.stock < 1}
                  >
                    <AddShoppingCartIcon />
                  </IconButton>
                </Box>
                {book.stock < 1 && (
                  <Typography color="error" variant="body2">
                    Stokta yok
                  </Typography>
                )}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      >
        <Alert severity={snackbar.severity} onClose={() => setSnackbar({ ...snackbar, open: false })}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default BookList;
