import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, Button, Box, Badge } from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import LibraryBooksIcon from '@mui/icons-material/LibraryBooks';
import axios from 'axios';

const Navbar = () => {
  const [user, setUser] = useState(null);
  const [cartCount, setCartCount] = useState(0);
  const navigate = useNavigate();

  // LocalStorage'daki değişiklikleri dinle
  useEffect(() => {
    const checkUser = () => {
      const storedUser = localStorage.getItem('user');
      if (storedUser) {
        const userData = JSON.parse(storedUser);
        setUser(userData);
        if (userData.role !== 'admin') {  // Admin değilse sepet sayısını getir
          fetchCartCount();
        }
      } else {
        setUser(null);
      }
    };

    // İlk yüklemede kontrol et
    checkUser();

    // Storage event listener ekle
    window.addEventListener('storage', checkUser);

    // Custom event listener ekle
    window.addEventListener('userLogin', checkUser);
    window.addEventListener('userLogout', checkUser);

    return () => {
      window.removeEventListener('storage', checkUser);
      window.removeEventListener('userLogin', checkUser);
      window.removeEventListener('userLogout', checkUser);
    };
  }, []);

  const fetchCartCount = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/cart', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCartCount(response.data.length);
    } catch (error) {
      console.error('Sepet sayısı alınamadı:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    window.dispatchEvent(new Event('userLogout'));
    navigate('/login');
  };

  return (
    <AppBar position="static">
      <Toolbar>
        <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
          Kitap Marketi
        </Typography>
        <Box>
          <Button color="inherit" component={Link} to="/">
            Ana Sayfa
          </Button>
          <Button color="inherit" component={Link} to="/books">
            Kitaplar
          </Button>
          {user ? (
            <>
              {user.role === 'admin' ? (
                // Admin menüsü
                <Button color="inherit" component={Link} to="/admin">
                  Admin Panel
                </Button>
              ) : (
                // Normal kullanıcı menüsü
                <>
                  <Button color="inherit" component={Link} to="/my-books">
                    <LibraryBooksIcon sx={{ mr: 1 }} />
                    Kitaplarım
                  </Button>
                  <Button color="inherit" component={Link} to="/cart">
                    <Badge badgeContent={cartCount} color="error">
                      <ShoppingCartIcon />
                    </Badge>
                  </Button>
                  <Button color="inherit" component={Link} to="/orders">
                    Siparişlerim
                  </Button>
                </>
              )}
              <Button color="inherit" onClick={handleLogout}>
                Çıkış Yap ({user.name})
              </Button>
            </>
          ) : (
            <>
              <Button color="inherit" component={Link} to="/login">
                Giriş Yap
              </Button>
              <Button color="inherit" component={Link} to="/register">
                Kayıt Ol
              </Button>
            </>
          )}
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
