import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Box
} from '@mui/material';
import axios from 'axios';

const MyBooks = () => {
  const [books, setBooks] = useState([]);
  const [open, setOpen] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [newBook, setNewBook] = useState({
    title: '',
    author: '',
    price: '',
    stock: '',
    description: '',
    category: '',
    image_url: ''
  });

  useEffect(() => {
    fetchMyBooks();
  }, []);

  const fetchMyBooks = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/my-books', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setBooks(response.data);
    } catch (error) {
      console.error('Kitaplar alınamadı:', error);
    }
  };

  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append('file', file);

      try {
        const token = localStorage.getItem('token');
        const response = await axios.post('http://localhost:5000/api/upload', formData, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setNewBook({ ...newBook, image_url: response.data.image_url });
      } catch (error) {
        console.error('Resim yüklenemedi:', error);
      }
    }
  };

  const handleSubmit = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5000/api/books', newBook, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOpen(false);
      fetchMyBooks();
      setNewBook({
        title: '',
        author: '',
        price: '',
        stock: '',
        description: '',
        category: '',
        image_url: ''
      });
    } catch (error) {
      console.error('Kitap eklenemedi:', error);
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
        <Typography variant="h4">Kitaplarım</Typography>
        <Button variant="contained" onClick={() => setOpen(true)}>
          Yeni Kitap Ekle
        </Button>
      </Box>

      <Grid container spacing={3}>
        {books.map((book) => (
          <Grid item xs={12} sm={6} md={4} key={book.id}>
            <Card>
              <CardMedia
                component="img"
                height="200"
                image={book.image_url || '/placeholder.png'}
                alt={book.title}
              />
              <CardContent>
                <Typography gutterBottom variant="h6">
                  {book.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Yazar: {book.author}
                </Typography>
                <Typography variant="body2">
                  Fiyat: {book.price} TL
                </Typography>
                <Typography variant="body2">
                  Stok: {book.stock}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Dialog open={open} onClose={() => setOpen(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Yeni Kitap Ekle</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            label="Kitap Adı"
            value={newBook.title}
            onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Yazar"
            value={newBook.author}
            onChange={(e) => setNewBook({ ...newBook, author: e.target.value })}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Fiyat"
            type="number"
            value={newBook.price}
            onChange={(e) => setNewBook({ ...newBook, price: e.target.value })}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Stok"
            type="number"
            value={newBook.stock}
            onChange={(e) => setNewBook({ ...newBook, stock: e.target.value })}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Açıklama"
            multiline
            rows={4}
            value={newBook.description}
            onChange={(e) => setNewBook({ ...newBook, description: e.target.value })}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Kategori"
            value={newBook.category}
            onChange={(e) => setNewBook({ ...newBook, category: e.target.value })}
            margin="normal"
          />
          <input
            accept="image/*"
            type="file"
            onChange={handleFileChange}
            style={{ marginTop: 16 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpen(false)}>İptal</Button>
          <Button onClick={handleSubmit} variant="contained">
            Ekle
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default MyBooks;
