import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  FormLabel,
  Input,
  IconButton,
  Box
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import axios from '../utils/axiosConfig';

const MyBooks = () => {
  const [books, setBooks] = useState([]);
  const [userInfo, setUserInfo] = useState({ balance: 0 });
  const [openDialog, setOpenDialog] = useState(false);
  const [editBook, setEditBook] = useState(null);
  const [newBook, setNewBook] = useState({
    title: '',
    author: '',
    price: '',
    stock: '',
    category: '',
    description: '',
    image: null
  });
  const [error, setError] = useState('');

  useEffect(() => {
    fetchMyBooks();
    fetchUserInfo();
  }, []);

  const fetchMyBooks = async () => {
    try {
      const response = await axios.get('/api/my-books');
      setBooks(response.data);
    } catch (error) {
      console.error('Kitaplar yüklenemedi:', error);
    }
  };

  const fetchUserInfo = async () => {
    try {
      const response = await axios.get('/api/user/info');
      setUserInfo(response.data);
    } catch (error) {
      console.error('Kullanıcı bilgileri alınamadı:', error);
    }
  };

  const handleAddBook = async (e) => {
    e.preventDefault();
    setError('');
    
    try {
      const formData = new FormData();
      formData.append('title', newBook.title);
      formData.append('author', newBook.author);
      formData.append('price', newBook.price);
      formData.append('stock', newBook.stock);
      formData.append('description', newBook.description);
      formData.append('category', newBook.category);
      if (newBook.image) {
        formData.append('image', newBook.image);
      }

      const response = await axios.post('/api/books', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setBooks([...books, response.data]);
      setNewBook({
        title: '',
        author: '',
        price: '',
        stock: '',
        description: '',
        category: '',
        image: null
      });
      setOpenDialog(false);
      
    } catch (error) {
      console.error('Kitap eklenemedi:', error);
      setError(error.response?.data?.error || 'Kitap eklenirken bir hata oluştu');
    }
  };

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setNewBook({ ...newBook, image: file });
    }
  };

  const handleDeleteBook = async (bookId) => {
    if (window.confirm('Bu kitabı silmek istediğinizden emin misiniz?')) {
      try {
        await axios.delete(`/api/books/${bookId}`);
        fetchMyBooks();
      } catch (error) {
        console.error('Kitap silinemedi:', error);
      }
    }
  };

  return (
    <Container sx={{ mt: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
        <Typography variant="h5">
          Kitaplarım
        </Typography>
        <Typography variant="h6" color="primary">
          Bakiye: {userInfo.balance?.toFixed(2)} TL
        </Typography>
      </Box>

      <Button 
        variant="contained" 
        color="primary" 
        onClick={() => setOpenDialog(true)}
        sx={{ mb: 3 }}
      >
        Yeni Kitap Ekle
      </Button>

      <Grid container spacing={3}>
        {books.map((book) => (
          <Grid item xs={12} sm={6} md={4} key={book.id}>
            <Card>
              <CardMedia
                component="img"
                height="200"
                image={book.image_url || '/placeholder.jpg'}
                alt={book.title}
                sx={{ objectFit: 'contain' }}
              />
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  {book.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Yazar: {book.author}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Kategori: {book.category || 'Belirtilmemiş'}
                </Typography>
                <Typography variant="h6" color="primary" sx={{ mt: 1 }}>
                  {book.price} TL
                </Typography>
                <Typography variant="body2">
                  Stok: {book.stock}
                </Typography>
                
                <div style={{ marginTop: '1rem' }}>
                  <IconButton 
                    size="small" 
                    onClick={() => setEditBook(book)}
                    color="primary"
                  >
                    <EditIcon />
                  </IconButton>
                  <IconButton 
                    size="small" 
                    onClick={() => handleDeleteBook(book.id)}
                    color="error"
                  >
                    <DeleteIcon />
                  </IconButton>
                </div>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>Yeni Kitap Ekle</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            label="Başlık"
            value={newBook.title}
            onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
            margin="normal"
            required
          />
          <TextField
            fullWidth
            label="Yazar"
            value={newBook.author}
            onChange={(e) => setNewBook({ ...newBook, author: e.target.value })}
            margin="normal"
            required
          />
          <TextField
            fullWidth
            label="Fiyat"
            type="number"
            value={newBook.price}
            onChange={(e) => setNewBook({ ...newBook, price: e.target.value })}
            margin="normal"
            required
          />
          <TextField
            fullWidth
            label="Stok"
            type="number"
            value={newBook.stock}
            onChange={(e) => setNewBook({ ...newBook, stock: e.target.value })}
            margin="normal"
            required
          />
          <TextField
            fullWidth
            label="Kategori"
            value={newBook.category}
            onChange={(e) => setNewBook({ ...newBook, category: e.target.value })}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Açıklama"
            multiline
            rows={4}
            value={newBook.description}
            onChange={(e) => setNewBook({ ...newBook, description: e.target.value })}
            margin="normal"
          />
          <FormControl fullWidth margin="normal">
            <FormLabel>Kitap Resmi</FormLabel>
            <Input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
            />
          </FormControl>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>İptal</Button>
          <Button onClick={handleAddBook} variant="contained">
            Ekle
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default MyBooks;
