import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Typography, 
  Button, 
  TextField, 
  Paper, 
  Table, 
  TableBody, 
  TableCell, 
  TableContainer, 
  TableHead, 
  TableRow,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Box
} from '@mui/material';
import axios from 'axios';

const AdminPanel = () => {
  const [books, setBooks] = useState([]);
  const [open, setOpen] = useState(false);
  const [newBook, setNewBook] = useState({
    title: '',
    author: '',
    price: '',
    stock: ''
  });
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/books');
      setBooks(response.data);
    } catch (error) {
      console.error('Kitaplar yüklenirken hata oluştu:', error);
    }
  };

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const handleInputChange = (e) => {
    setNewBook({
      ...newBook,
      [e.target.name]: e.target.value
    });
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setPreviewUrl(URL.createObjectURL(file));
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/books/${id}`);
      fetchBooks(); // Listeyi yenile
    } catch (error) {
      console.error('Kitap silinirken hata oluştu:', error);
    }
  };

  const handleSubmit = async () => {
    try {
      let imageUrl = '';
      
      if (selectedFile) {
        const formData = new FormData();
        formData.append('file', selectedFile);
        
        const uploadResponse = await axios.post('http://localhost:5000/api/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        
        imageUrl = `http://localhost:5000${uploadResponse.data.image_url}`;
      }

      await axios.post('http://localhost:5000/api/books', {
        ...newBook,
        price: parseFloat(newBook.price),
        stock: parseInt(newBook.stock),
        image_url: imageUrl
      });

      handleClose();
      fetchBooks();
      setNewBook({ title: '', author: '', price: '', stock: '' });
      setSelectedFile(null);
      setPreviewUrl('');
    } catch (error) {
      console.error('Kitap eklenirken hata oluştu:', error);
    }
  };

  return (
    <Container sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom>
        Admin Paneli
      </Typography>
      
      <Button variant="contained" color="primary" onClick={handleOpen} sx={{ mb: 4 }}>
        Yeni Kitap Ekle
      </Button>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Başlık</TableCell>
              <TableCell>Yazar</TableCell>
              <TableCell>Fiyat</TableCell>
              <TableCell>Stok</TableCell>
              <TableCell>Resim</TableCell>
              <TableCell>İşlemler</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {books.map((book) => (
              <TableRow key={book.id}>
                <TableCell>{book.id}</TableCell>
                <TableCell>{book.title}</TableCell>
                <TableCell>{book.author}</TableCell>
                <TableCell>{book.price} TL</TableCell>
                <TableCell>{book.stock}</TableCell>
                <TableCell>
                  {book.image_url && (
                    <img 
                      src={book.image_url} 
                      alt={book.title} 
                      style={{ height: '50px' }}
                    />
                  )}
                </TableCell>
                <TableCell>
                  <Button 
                    color="error" 
                    variant="contained" 
                    size="small"
                    onClick={() => handleDelete(book.id)}
                  >
                    Sil
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Yeni Kitap Ekle</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            name="title"
            label="Kitap Adı"
            fullWidth
            variant="outlined"
            value={newBook.title}
            onChange={handleInputChange}
          />
          <TextField
            margin="dense"
            name="author"
            label="Yazar"
            fullWidth
            variant="outlined"
            value={newBook.author}
            onChange={handleInputChange}
          />
          <TextField
            margin="dense"
            name="price"
            label="Fiyat"
            type="number"
            fullWidth
            variant="outlined"
            value={newBook.price}
            onChange={handleInputChange}
          />
          <TextField
            margin="dense"
            name="stock"
            label="Stok"
            type="number"
            fullWidth
            variant="outlined"
            value={newBook.stock}
            onChange={handleInputChange}
          />
          
          <Box sx={{ mt: 2, mb: 2 }}>
            <input
              accept="image/*"
              style={{ display: 'none' }}
              id="raised-button-file"
              type="file"
              onChange={handleFileSelect}
            />
            <label htmlFor="raised-button-file">
              <Button variant="contained" component="span">
                Resim Seç
              </Button>
            </label>
            
            {previewUrl && (
              <Box sx={{ mt: 2 }}>
                <img 
                  src={previewUrl} 
                  alt="Önizleme" 
                  style={{ maxWidth: '100%', maxHeight: '200px' }} 
                />
              </Box>
            )}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>İptal</Button>
          <Button onClick={handleSubmit} variant="contained">Ekle</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default AdminPanel;
